// ============================================
// TRANSFORM TO BIND ERP API FORMAT
// ============================================

const requisition = $input.item.json;

// Parse items from JSONB
const items = typeof requisition.requisition_items === 'string'
  ? JSON.parse(requisition.requisition_items)
  : requisition.requisition_items;

// ============================================
// VALORES FIJOS DE BIND (desde bind_mappings)
// ============================================
// Estos IDs son fijos para ComerECO en BIND ERP
// Si son null, usar valores por defecto del ambiente de prueba

const BIND_DEFAULTS = {
  // IDs fijos que NO cambian por proyecto
  AddressID: "efff1744-da2f-499d-952f-20b9140a95bf",     // Direcci√≥n de ComerECO
  CurrencyID: "b7e2c065-bd52-40ca-b508-3accdd538860",    // MXN (Peso Mexicano)

  // IDs que vienen de bind_mappings o usar defaults
  ClientID: requisition.bind_client_id || "d02c1c47-c9a5-4728-93a0-29e6b6136a15",
  LocationID: requisition.bind_location_id || "d7ef64f2-fd1e-437a-bd93-af01985be5a5",
  PriceListID: requisition.bind_price_list_id || "1d5f1d2f-7cd1-4e44-83cc-d47789f70b51",
  WarehouseID: requisition.bind_warehouse_id || "a8605382-7b48-47e2-9fb6-a25cfb7cf735"
};

// ============================================
// TRANSFORM PRODUCTS
// ============================================
// Convertir items de requisici√≥n al formato Products de BIND

const bindProducts = items.map(item => ({
  ID: item.bind_product_id || item.product_id,  // ID del producto en BIND
  Price: parseFloat(item.unit_price || 0),
  Qty: parseInt(item.quantity || 1),
  VAT: 0,  // IVA por defecto (puede cambiar seg√∫n producto)
  Comments: item.comments || "",
  Unit: item.unit || "pza"
}));

// ============================================
// BUILD BIND API PAYLOAD
// ============================================
// Formato EXACTO que espera BIND ERP API

const bindPayload = {
  AddressID: BIND_DEFAULTS.AddressID,
  ClientID: BIND_DEFAULTS.ClientID,
  CurrencyID: BIND_DEFAULTS.CurrencyID,
  LocationID: BIND_DEFAULTS.LocationID,
  OrderDate: new Date().toISOString(),
  PriceListID: BIND_DEFAULTS.PriceListID,
  WarehouseID: BIND_DEFAULTS.WarehouseID,
  Comments: requisition.comments || `Requisici√≥n ${requisition.internal_folio} - COMERECO`,
  PurchaseOrder: requisition.internal_folio,  // Usar folio interno como referencia
  Products: bindProducts,
  Services: []  // Por ahora sin servicios
};

// ============================================
// METADATA PARA LOGGING
// ============================================

const metadata = {
  requisition_id: requisition.requisition_id,
  company_id: requisition.company_id,
  internal_folio: requisition.internal_folio,
  sync_attempt: (requisition.bind_sync_attempts || 0) + 1,
  n8n_execution_id: $execution.id,
  timestamp: new Date().toISOString(),
  bind_mappings_used: {
    client_id: requisition.bind_client_id || 'DEFAULT',
    location_id: requisition.bind_location_id || 'DEFAULT',
    price_list_id: requisition.bind_price_list_id || 'DEFAULT',
    warehouse_id: requisition.bind_warehouse_id || 'DEFAULT'
  }
};

console.log('‚úÖ Transformaci√≥n BIND completada:');
console.log('  Requisici√≥n:', requisition.internal_folio);
console.log('  Productos:', bindProducts.length);
console.log('  ClientID:', BIND_DEFAULTS.ClientID);

return {
  json: {
    bind_payload: bindPayload,
    metadata: metadata
  }
};

// ============================================
// CAPTURAR ID DE LA ORDEN CREADA EN BIND
// ============================================
// BIND POST response solo regresa: {"data": "order-uuid"}

const postResponse = $input.first().json;
const metadata = $('Transform to BIND Format').item.json.metadata;

// El POST regresa solo el UUID como texto en el campo "data"
const orderId = postResponse.data;

if (!orderId) {
  console.error('‚ùå ERROR: No se recibi√≥ ID de la orden');
  console.error('Response recibida:', JSON.stringify(postResponse));
  throw new Error('No se recibi√≥ order ID de BIND API');
}

console.log('‚úÖ Orden creada exitosamente en BIND');
console.log('üìã Order ID:', orderId);
console.log('üì¶ Requisici√≥n:', metadata.internal_folio);

return {
  json: {
    orderId: orderId,
    requisition_id: metadata.requisition_id,
    company_id: metadata.company_id,
    internal_folio: metadata.internal_folio,
    sync_attempt: metadata.sync_attempt,
    timestamp: metadata.timestamp
  }
};
